import work from '../images/menu-icons/work.png'
import github from '../images/menu-icons/github.png'
import contact from '../images/menu-icons/contact.png'

const Menu = ({handleClick}) => {
    const menuItems = [
        {
            name: 'Home',
            icon: `<svg version="1.1" baseProfile="tiny" xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink"
            x="0px" y="0px" viewBox="0 0 512 512" xmlSpace="preserve">
                   <g>
                       <path d="M506.6,208.1L263.9,30.4c-4.7-3.4-11-3.4-15.7,0L5.4,208.1c-5.9,4.3-7.2,12.7-2.9,18.6s12.7,7.2,18.6,2.9L256,57.6
                               l234.8,171.9c2.4,1.7,5.1,2.6,7.8,2.6c4.1,0,8.1-1.9,10.7-5.4C513.8,220.7,512.5,212.4,506.6,208.1z"/>

