import React, {useState} from 'react'
import Contactform from './Contactform'

const Contact = () => {
    const [ newName, setNewName ] = useState('')
    const [ newEmail, setNewEmail ] = useState('')
    const [ clientMessage, setClientMessage ] = useState('')

    const handleSubmit = (e) => {
        //stop page reloading
        e.preventDefault()
        //store inputs in mailobject
        const mailObject = {
            name: newName,
            mail: newEmail,
            message: clientMessage
        }
        console.log(mailObject)
        //reset fields
        setNewName('')
        setNewEmail('')
        setClientMessage('')
    }

    const showInput = (e) => {
        //in what field is user typing
        const whatChanged = e.target.getAttribute('name')
        //what is user typing
        const newInput = e.target.value
        //change correct state
        switch(whatChanged) {
            case 'name':
                setNewName(newInput)
                break;
            case 'email':
                setNewEmail(newInput)
                break;
            case 'message':
                setClientMessage(newInput)
        }
    }

    return (
        <div className="contact-container">
            <div className="page-header">
                <h1>Contact me</h1>
                <p>Got any questions?</p>
                <p>Send me a message and I'll get back to you as soon as possible.</p>
            </div>

            <Contactform
                submit={handleSubmit}
                name={newName}
                email={newEmail}
                message={clientMessage}
                change={showInput}
            />

            {/* <div className="form-container">
