import React, {useState} from 'react'
import Menu from './components/Menu'
import Content from './components/Content'

function App() {
  const [display, setDisplay] = useState('Home')

  //Textcontent van p meegeven als display, div class active geven
  const changeDisplay = (event) => {
    let selected = ''

    const tag = event.target.tagName

    if(tag.includes('DIV')){
      selected = selected.lastChild.textContent
      console.log('div')  
      console.log(selected)
    } else {
      selected = selected.parentNode.lastChild.textContent
      console.log('img of p')
      console.log(selected)
    }

    setDisplay(selected)
  }

  return (
    <div className="App">
      <Menu handleClick={changeDisplay}/>
      <Content display={display}/>
    </div>
  );
}

export default App;
