import React, {useState} from 'react'
import Menu from './components/Menu'
import Content from './components/Content'

function App() {
  const [display, setDisplay] = useState('Home')

  // Textcontent van p meegeven als display, div class active geven
  const changeDisplay = (event) => {
    let selected = '';
    let click = event.target;
    console.log(event.target.tagName)

    if(click.tagName === 'path') {
      selected = click.parentNode.classList.value
    } else {
      selected = click.classList.value
    }

    switch (click.tagName) {
      case 'path':
        selected = click.parentNode.classList.value
        break;
      case 'svg':
        selected = click.classList.value
        break;
      default:
        const splitted = click.classList.value.split(' ')
        selected = splitted.slice(-1)[0]
    }

    (click.tagName === 'path') ? selected = click.parentNode.classList.value : selected = click.classList.value
    //(click.tagName.includes('DIV')) ? selected = click.lastChild.textContent : selected = click.parentNode.lastChild.textContent;
    setDisplay(selected)
    console.log(selected)

  }

  return (
    <div className="App">
      <Menu handleClick={changeDisplay}/>
      <Content display={display}/>
    </div>
  );
}

export default App;
