import React, {useState} from 'react'
import Menu from './components/Menu'
import Content from './components/Content'

function App() {
  const [display, setDisplay] = useState('Home');

  const removePreviousActive = () => {
    if (document.querySelector('.menu-item.active-menu')) {
      const test = document.querySelector('.menu-item.active-menu');
      test.classList.remove('active-menu');
    } else {
      return
    }

  }

  // Textcontent van p meegeven als display, div class active geven
  const changeDisplay = (event) => {
    let selected = '';
    let click = event.target;

    removePreviousActive()

    switch (click.tagName) {
      case 'path':
        selected = click.parentNode.classList.value
        click.parentNode.parentNode.classList.add("active-menu")
        break;
      case 'svg':
        selected = click.classList.value
        click.parentNode.classList.add("active-menu")
        break;
      case 'P':
        selected = click.classList.value.split(' ').slice(-1)[0]
        click.parentNode.classList.add("active-menu")
        console.log(click.parentNode)
        break;
      case 'DIV':
        selected = click.classList.value.split(' ').slice(-1)[0]
        click.classList.add("active-menu")
        console.log(click)
    }

    setDisplay(selected)

  }


  return (
    <div className="App">
      <Menu handleClick={changeDisplay}/>
      <Content display={display}/>
    </div>
  );
}

export default App;
